import React, { useState, useEffect } from 'react';
import { Button, Form } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Employees from './Employees';
import { v4 as uuid } from "uuid";
import { Link, useNavigate } from 'react-router-dom'

function Edit() {

    const [name, setName] = useState('');
    const [contact, setContact] = useState('');
    const [room, setRoom] = useState('');
    const [bill, setBill] = useState('');
    const [id, setId] = useState('');

    let history = useNavigate();

    var index = Employees.map(function (e) {
        return e.id
    }).indexOf(id);

    const handleSubmit = (e) => {
        e.preventDefault();

        let a = Employees[index];
        a.Name = name;
        a.Contact = contact;
        a.Room = room;
        a.Bill = bill;

        history("/home");
    }

    useEffect(() => {
        setName(localStorage.getItem('Name'))
        setContact(localStorage.getItem('Contact'))
        setRoom(localStorage.getItem('Room'))
        setBill(localStorage.getItem('Bill'))
        setId(localStorage.getItem('Id'))
    }, [])

    return (
        <div>

            <section class="main_heading " id="Patient-list">
                <div class="text-center">
                    <h1 id="contactus" class="display-3">Add Details</h1>
                    <hr class="w-25  mx-auto bg-dark" style={{ height: "3px" }} />

                    <Form className="d-grid gap-2" style={{ margin: "15rem" }}>
                        <Form.Group className="mb-3" controlID="formName">
                            <Form.Control type="text" placeholder="Enter Name" required onChange={(e) => setName(e.target.value)}>
                            </Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-3" controlID="formContact">
                            <Form.Control type="text" placeholder="Enter Contact" required onChange={(e) => setContact(e.target.value)}>
                            </Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-3" controlID="formRoom">
                            <Form.Control type="text" placeholder="Enter Room" required onChange={(e) => setRoom(e.target.value)}>
                            </Form.Control>
                        </Form.Group>
                        <Form.Group className="mb-3" controlID="formBill">
                            <Form.Control type="text" placeholder="Enter Bill" required onChange={(e) => setBill(e.target.value)}>
                            </Form.Control>
                        </Form.Group>
                        <Button onClick={(e) => handleSubmit(e)} type="submit">Update</Button>
                    </Form>
                </div>
            </section>
        </div>
    )

}
export default Edit;
