import react, {Fragment} from 'react';
import {Button, Table} from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Employees from './Employees';
import {Link, useNavigate} from 'react-router-dom'

function Home(){

    let history = useNavigate();

    const handleEdit = (id, name, contact, room, bill) => {
        localStorage.setItem('Name',name);
        localStorage.setItem('Contact',contact);
        localStorage.setItem('Room',room);
        localStorage.setItem('Bill',bill);
        localStorage.setItem('Id',id);


    }

    const handleDelete = (id) => {
        var index = Employees.map(function(e) {
            return e.id
        }).indexOf(id);

        Employees.splice(index,1);

        history('/')
    }

    return(
        <Fragment>
            <div style={{margin:"10rem"}}>
                <Table className='dark' striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>
                                Name
                            </th>
                            <th>
                                Contact
                            </th>
                            <th>
                                Room
                            </th>
                            <th>
                                Bill
                            </th>
                            <th>
                                Actions
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            Employees && Employees.length > 0
                            ?
                            Employees.map((item) =>{
                                return(
                                    <tr>
                                        <td>
                                            {item.Name}  
                                        </td>
                                        <td>
                                            {item.Contact}  
                                        </td>
                                        <td>
                                            {item.Room}  
                                        </td>
                                        <td>
                                            {item.Bill}  
                                        </td>
                                        <td>
                                            <Link to={'/edit '}>
                                            <Button onClick={()=>handleEdit(item.id, item.Name, item.Contact, item.Room, item.Bill)}>Edit</Button>
                                            </Link>
                                            &nbsp;
                                            <Button onClick={()=>handleDelete(item.id)}>Delete</Button>
                                        </td>
                                    </tr>
                                )
                            })
                            :
                            "No data available"
                        }
                    </tbody>
                </Table>
                <br>
                </br>
                <Link className='gap-2' to="add">
                    <Button size="lg">Add</Button>
                </Link>
            </div>
        </Fragment>
    )
}

export default Home;
