import react, { Fragment } from 'react';
import { Button, Table } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Employees from './Employees';
import { Link, useNavigate } from 'react-router-dom'

function Home() {

    let history = useNavigate();

    const handleEdit = (id, name, contact, room, bill) => {
        localStorage.setItem('Name', name);
        localStorage.setItem('Contact', contact);
        localStorage.setItem('Room', room);
        localStorage.setItem('Bill', bill);
        localStorage.setItem('Id', id);


    }

    const handleDelete = (id) => {
        var index = Employees.map(function (e) {
            return e.id
        }).indexOf(id);

        Employees.splice(index, 1);

        history('/home')
    }

    return (
        <Fragment>

            <section class="main_heading  my-2" id="Patient-list">
                <div class="text-center">
                    <h1 id="contactus" class="display-4">Patients List</h1>
                    <hr class="w-25  mx-auto bg-dark" style={{ height: "3px" }} />

                    <div style={{ margin: "10rem" }}>
                        <Table striped bordered hover size="sm">
                            <thead style={{ color: "#FFFFFF", backgroundColor: "#457" }}>
                                <tr>
                                    <th>
                                        Name
                                    </th>
                                    <th>
                                        Contact
                                    </th>
                                    <th>
                                        Room
                                    </th>
                                    <th>
                                        Bill
                                    </th>
                                    <th>
                                        Actions
                                    </th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    Employees && Employees.length > 0
                                        ?
                                        Employees.map((item) => {
                                            return (
                                                <tr>
                                                    <td>
                                                        {item.Name}
                                                    </td>
                                                    <td>
                                                        {item.Contact}
                                                    </td>
                                                    <td>
                                                        {item.Room}
                                                    </td>
                                                    <td>
                                                        {item.Bill}
                                                    </td>
                                                    <td>
                                                        <Link to={'/edit '}>
                                                            <Button onClick={() => handleEdit(item.id, item.Name, item.Contact, item.Room, item.Bill)} style={{ color: "#FFFFFF", backgroundColor: "#457" }}>Edit</Button>
                                                        </Link>
                                                        &nbsp;
                                                        <Button onClick={() => handleDelete(item.id)} style={{ color: "#FFFFFF", backgroundColor: "#3E3A3A" }}>Delete</Button>
                                                    </td>
                                                </tr>
                                            )
                                        })
                                        :
                                        "No data available"
                                }
                            </tbody>
                        </Table>
                        <br>
                        </br>
                        <Link className='gap-2' to={"/add"}>
                            <Button size="lg" style={{ color: "#FFFFFF", backgroundColor: "#457" }}>Add</Button>
                        </Link>
                    </div>
                    </div>
            </section>
        </Fragment>
    )
}

export default Home;
